<?php  if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Pledgie {	function Pledgie($config)	{		foreach ($config as $key => $value)		{			$this->$key = $value;		}	}	function raised()	{		if ($pledgie = @file_get_contents('http://pledgie.com/campaigns/'.$this->campaign.'.xml'))		{			preg_match('@<amount-raised type="float">([0-9\.]+)</amount-raised>@', $pledgie, $matches);			return "So far we've raised <strong>\$${matches[1]}</strong>. ";		}		return '';	}}/* End of file Pledgie.php *//* Location: ./system/application/modules/matchbox/libraries/Pledgie.php */